package threadpools;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;/** * Created by demopoo on 2017/9/21. */public class SharedThreadPools {    public static ExecutorService executorService;    public static ExecutorService getExcutor(){        if (executorService == null){            /*根据需要来动态创建线程池的数量，若之前有创建过的线程处于可用状态。会自动提取使用             *若创建的线程60秒没有使用过，系统自动销毁。并清除缓存             */            executorService = Executors.newCachedThreadPool();        }        return executorService;    }}